mapper.select:
select count(*) from t_work_bill 
where C_PICKSTATE = '0' 

and C_BUILDTIME < #{date}

select 
from t_work_bill
where C_PICKSTATE = '0'

and C_BUILDTIME < #{date}

limit #{pageNum},#{pageSize}

select count(*) from t_work_bill 
where C_PICKSTATE = '0' and C_COURIER = #{courierId}

and C_BUILDTIME < #{date}

select 
from t_work_bill
where C_PICKSTATE = '0' and C_COURIER = #{courierId}

and C_BUILDTIME < #{date}

limit #{pageNum},#{pageSize}

mapper.update:
update t_work_bill
set C_PICKSTATE = 1
where C_ID = #{id}
